mapper.select:
SELECT RNUM, SEQ, ID, REF, STEP, DEPTH, TITLE, CONTENT, WDATE, PARENT, DEL, READCOUNT FROM ( SELECT ROWNUM AS RNUM, SEQ, ID, REF, STEP, DEPTH, TITLE, CONTENT, WDATE, PARENT, DEL, READCOUNT FROM ( SELECT SEQ, ID, REF, STEP, DEPTH, TITLE, CONTENT, WDATE, PARENT, DEL, READCOUNT FROM BBS190729 WHERE DEL = 0
AND #{condquery} LIKE '%'||#{keyword}||'%'
ORDER BY SEQ DESC ) ORDER BY RNUM ) WHERE RNUM BETWEEN #{startSeq} AND #{endSeq}
